import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(""); // Mensaje de error
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ correo: email, contraseña: password }),
      });
  
      if (!response.ok) {
        const errorData = await response.json();
        setMessage(errorData.message || "Credenciales incorrectas.");
        return;
      }
  
      const data = await response.json();
  
      // Verifica que `data.user` tiene los campos necesarios
      if (data.user) {
        // Guarda todos los datos del usuario en localStorage
        localStorage.setItem("user", JSON.stringify(data.user));
  
        // Redirige al dashboard
        navigate("/dashboard");
      } else {
        setMessage("Error: Datos de usuario no disponibles.");
      }
    } catch (error) {
      console.error("Error al iniciar sesión:", error);
      setMessage("Ocurrió un error al iniciar sesión. Intenta nuevamente.");
    }
  };
  

  return (
    <div>
      <h1>Iniciar Sesión</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Correo"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Iniciar sesión</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
