import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState(""); // Mensaje de éxito o error
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3000/api/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ correo: email, contraseña: password }),
            });
    
            // Comprobar el tipo de contenido de la respuesta
            const contentType = response.headers.get("content-type");
    
            if (contentType && contentType.includes("application/json")) {
                const data = await response.json();
    
                if (response.ok) {
                    setMessage("Inicio de sesión exitoso");
                    localStorage.setItem("token", data.token); // Guardar el token en localStorage
                    localStorage.setItem("user", JSON.stringify(data.user)); // Guardar datos del usuario
                    navigate("/dashboard"); // Redirigir al dashboard
                } else {
                    setMessage(data.message || "Error al iniciar sesión.");
                }
            } else {
                const text = await response.text();
                console.error("Respuesta no válida del servidor:", text);
                setMessage("Error inesperado en el servidor. Verifica la consola para más detalles.");
            }
        } catch (error) {
            console.error("Error en el inicio de sesión:", error);
            setMessage("No se pudo conectar con el servidor. Intenta nuevamente.");
        }
    };
    
    return (
        <div className="login-container">
            <h1 className="login-title">Iniciar Sesión</h1>
            <form className="login-form" onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="Correo"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit" className="login-button">Iniciar sesión</button>
            </form>
            <a href="/register" className="login-link">Registrarse</a>
            {message && <p className="login-message">{message}</p>}
        </div>
    );
};

export default Login;
