import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
    const [formData, setFormData] = useState({
        correo: "",
        contraseña: "",
    });

    const [message, setMessage] = useState(""); // Mensaje de éxito o error
    const [retry, setRetry] = useState(false); // Mostrar enlace para reintentar
    const navigate = useNavigate(); // Para redirigir

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3000/api/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage("Inicio de sesión exitoso");
                setRetry(false);
                // Redirigir al dashboard
                navigate("/dashboard");
            } else {
                setMessage(data.message || "Credenciales incorrectas. Reintentar.");
                setRetry(true); // Activar opción para reintentar
            }
        } catch (error) {
            console.error("Error en el inicio de sesión:", error);
            setMessage("Error al iniciar sesión. Intenta nuevamente.");
            setRetry(true); // Activar opción para reintentar
        }
    };

    const handleRetry = () => {
        setMessage("");
        setRetry(false);
    };

    return (
        <div className="login-container">
            <h1 className="login-title">Iniciar Sesión</h1>
            <form className="login-form" onSubmit={handleSubmit}>
                <input
                    type="email"
                    name="correo"
                    placeholder="Correo"
                    value={formData.correo}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="contraseña"
                    placeholder="Contraseña"
                    value={formData.contraseña}
                    onChange={handleChange}
                />
                <button type="submit" className="login-button">Iniciar sesión</button>
            </form>
            <a href="/register" className="login-link">Registrarse</a>
            {message && (
                <p className="login-message">
                    {message}
                    {retry && (
                        <span
                            className="retry-link"
                            onClick={handleRetry}
                            style={{ cursor: "pointer", color: "#2196f3", marginLeft: "10px" }}
                        >
                            Reintentar
                        </span>
                    )}
                </p>
            )}
        </div>
    );
};

export default Login;
