import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import Register from "./views/Register";
import Login from "./views/login";
import Dashboard from "./views/Dashboard"; // Dashboard
import Header from "./components/header"; // Header para Login y Registro
import MainHeader from "./components/MainHeader"; // Nuevo Header para Dashboard y otras páginas

const App = () => {
    const location = useLocation(); // Detectar la ubicación actual

    const isAuthPage = location.pathname === "/login" || location.pathname === "/register"; // Verifica si es login o registro

    return (
        <>
            {/* Renderiza el header adecuado según la ruta */}
            {isAuthPage ? <Header /> : <MainHeader />}

            <Routes>
                <Route path="/" element={<Navigate to="/login" />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/dashboard" element={<Dashboard />} /> {/* Ruta para Dashboard */}
            </Routes>
        </>
    );
};

export default App;
