import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState(""); // Mensaje de éxito o error
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3000/api/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ correo: email, contraseña: password }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setMessage(errorData.message || "Error al iniciar sesión.");
                return;
            }

            const { token, user } = await response.json();

            // Guardar el token en localStorage para uso futuro
            localStorage.setItem("token", token);
            localStorage.setItem("user", JSON.stringify(user));

            setMessage("Inicio de sesión exitoso");
            navigate("/dashboard"); // Redirigir al dashboard
        } catch (error) {
            console.error("Error en el inicio de sesión:", error);
            setMessage("Error en el servidor. Intenta nuevamente.");
        }
    };

    return (
        <div className="login-container">
            <h1 className="login-title">Iniciar Sesión</h1>
            <form className="login-form" onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="Correo"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit" className="login-button">Iniciar sesión</button>
            </form>
            <a href="/register" className="login-link">Registrarse</a>
            {message && <p className="login-message">{message}</p>}
        </div>
    );
};

export default Login;
