import React, { useState } from "react";
import "./Register.css";

const Register = () => {
    const [formData, setFormData] = useState({
        nombre_completo: "",
        celular: "",
        correo: "",
        documento_identidad: "",
        contraseña: "",
        confirmar_contraseña: "",
        congregacion_actual: "",
        distrito: "",
        años_de_ministerio: "",
    });

    const [message, setMessage] = useState(""); // Mensaje de éxito o error
    const [errors, setErrors] = useState({}); // Errores de validación
    const [showPassword, setShowPassword] = useState(false); // Estado para mostrar/ocultar contraseñas

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validate = () => {
        const newErrors = {};

        if (!formData.nombre_completo.trim()) {
            newErrors.nombre_completo = "El nombre completo es obligatorio.";
        }

        if (!formData.celular.trim()) {
            newErrors.celular = "El celular es obligatorio.";
        } else if (!/^\d{10}$/.test(formData.celular)) {
            newErrors.celular = "El celular debe tener 10 dígitos.";
        }

        if (!formData.correo.trim()) {
            newErrors.correo = "El correo es obligatorio.";
        } else if (!/\S+@\S+\.\S+/.test(formData.correo)) {
            newErrors.correo = "El formato del correo no es válido.";
        }

        if (!formData.documento_identidad.trim()) {
            newErrors.documento_identidad = "El documento de identidad es obligatorio.";
        }

        if (!formData.contraseña.trim()) {
            newErrors.contraseña = "La contraseña es obligatoria.";
        } else if (formData.contraseña.length < 8) {
            newErrors.contraseña = "La contraseña debe tener al menos 8 caracteres.";
        }

        if (formData.contraseña !== formData.confirmar_contraseña) {
            newErrors.confirmar_contraseña = "Las contraseñas no coinciden.";
        }

        if (!formData.congregacion_actual.trim()) {
            newErrors.congregacion_actual = "La congregación actual es obligatoria.";
        }

        if (!formData.distrito.trim()) {
            newErrors.distrito = "El distrito es obligatorio.";
        }

        if (!formData.años_de_ministerio.trim()) {
            newErrors.años_de_ministerio = "Los años de ministerio son obligatorios.";
        } else if (isNaN(formData.años_de_ministerio) || formData.años_de_ministerio < 0) {
            newErrors.años_de_ministerio = "Los años de ministerio deben ser un número positivo.";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validate()) {
            setMessage("Por favor corrige los errores.");
            return;
        }

        try {
            const response = await fetch("http://localhost:3000/api/register", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            });

            const data = await response.json();
            if (response.ok) {
                setMessage("¡Usuario registrado con éxito!");
                setFormData({
                    nombre_completo: "",
                    celular: "",
                    correo: "",
                    documento_identidad: "",
                    contraseña: "",
                    confirmar_contraseña: "",
                    congregacion_actual: "",
                    distrito: "",
                    años_de_ministerio: "",
                });
                setErrors({});
            } else {
                setMessage(`Error: ${data.message}`);
            }
        } catch (error) {
            console.error("Error:", error);
            setMessage("Error al registrar el usuario. Intenta nuevamente.");
        }
    };

    const toggleShowPassword = () => {
        setShowPassword(!showPassword);
    };

    return (
        <div className="register-container">
            <h1 className="register-title">Registro</h1>
            <form className="register-form" onSubmit={handleSubmit}>
                <div className="register-section">
                    <h2 className="register-subtitle">Información personal</h2>
                    <input
                        type="text"
                        name="nombre_completo"
                        placeholder="Nombre Completo"
                        value={formData.nombre_completo}
                        onChange={handleChange}
                    />
                    {errors.nombre_completo && <p className="error-message">{errors.nombre_completo}</p>}

                    <input
                        type="text"
                        name="celular"
                        placeholder="Celular"
                        value={formData.celular}
                        onChange={handleChange}
                    />
                    {errors.celular && <p className="error-message">{errors.celular}</p>}

                    <input
                        type="email"
                        name="correo"
                        placeholder="Correo"
                        value={formData.correo}
                        onChange={handleChange}
                    />
                    {errors.correo && <p className="error-message">{errors.correo}</p>}

                    <input
                        type="text"
                        name="documento_identidad"
                        placeholder="Documento de identidad"
                        value={formData.documento_identidad}
                        onChange={handleChange}
                    />
                    {errors.documento_identidad && <p className="error-message">{errors.documento_identidad}</p>}

                    <input
                        type={showPassword ? "text" : "password"}
                        name="contraseña"
                        placeholder="Contraseña"
                        value={formData.contraseña}
                        onChange={handleChange}
                    />
                    {errors.contraseña && <p className="error-message">{errors.contraseña}</p>}

                    <input
                        type={showPassword ? "text" : "password"}
                        name="confirmar_contraseña"
                        placeholder="Confirmar Contraseña"
                        value={formData.confirmar_contraseña}
                        onChange={handleChange}
                    />
                    {errors.confirmar_contraseña && <p className="error-message">{errors.confirmar_contraseña}</p>}

                    <button
                        type="button"
                        className="toggle-password-button"
                        onClick={toggleShowPassword}
                    >
                        {showPassword ? "Ocultar Contraseña" : "Mostrar Contraseña"}
                    </button>
                </div>

                <div className="register-section">
                    <h2 className="register-subtitle">Información ministerial</h2>
                    <input
                        type="text"
                        name="congregacion_actual"
                        placeholder="Congregación actual"
                        value={formData.congregacion_actual}
                        onChange={handleChange}
                    />
                    {errors.congregacion_actual && <p className="error-message">{errors.congregacion_actual}</p>}

                    <input
                        type="text"
                        name="distrito"
                        placeholder="Distrito al que pertenece"
                        value={formData.distrito}
                        onChange={handleChange}
                    />
                    {errors.distrito && <p className="error-message">{errors.distrito}</p>}

                    <input
                        type="number"
                        name="años_de_ministerio"
                        placeholder="Años de ministerio"
                        value={formData.años_de_ministerio}
                        onChange={handleChange}
                    />
                    {errors.años_de_ministerio && <p className="error-message">{errors.años_de_ministerio}</p>}
                </div>

                <div className="register-actions">
                    <button type="submit" className="register-button">Registrarse</button>
                    <a href="#" className="register-link">Iniciar sesión</a>
                </div>
            </form>
            {message && <p className="register-message">{message}</p>} {/* Mensaje de éxito o error */}
        </div>
    );
};

export default Register;
