import React from "react";
import { Link, useLocation } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
    const location = useLocation();

    // Define las rutas activas para destacar la ruta actual
    const isActive = (path) => location.pathname === path;

    return (
        <nav className="navbar">
            <ul className="navbar-menu">
                <li className={isActive("/dashboard") ? "active" : ""}>
                    <Link to="/dashboard">Dashboard</Link>
                </li>
                <li className={isActive("/encuesta") ? "active" : ""}>
                    <Link to="/encuesta">Encuesta</Link>
                </li>
                <li className={isActive("/lideres") ? "active" : ""}>
                    <Link to="/lideres">Líderes</Link>
                </li>
                <li className={isActive("/calendario") ? "active" : ""}>
                    <Link to="/calendario">Calendario</Link>
                </li>
                <li>
                    <Link to="/logout">Cerrar sesión</Link>
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
