import React, { useState } from "react";
import './Register.css';


const Register = () => {
    const [formData, setFormData] = useState({
        nombreCompleto: "",
        celular: "",
        correo: "",
        documentoIdentidad: "",
        contraseña: "",
        congregacionActual: "",
        distrito: "",
        añosDeMinisterio: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3000/api/register", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            });
            const data = await response.json();
            alert(data.message);
        } catch (error) {
            console.error("Error:", error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="register-form">
            <div>
                <h2>Información personal</h2>
                <input
                    type="text"
                    name="nombreCompleto"
                    placeholder="Nombre Completo"
                    value={formData.nombreCompleto}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="celular"
                    placeholder="Celular"
                    value={formData.celular}
                    onChange={handleChange}
                />
                <input
                    type="email"
                    name="correo"
                    placeholder="Correo"
                    value={formData.correo}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="documentoIdentidad"
                    placeholder="Documento de identidad"
                    value={formData.documentoIdentidad}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="contraseña"
                    placeholder="Contraseña"
                    value={formData.contraseña}
                    onChange={handleChange}
                />
            </div>
            <div>
                <h2>Información ministerial</h2>
                <input
                    type="text"
                    name="congregacionActual"
                    placeholder="Congregación actual"
                    value={formData.congregacionActual}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="distrito"
                    placeholder="Distrito al que pertenece"
                    value={formData.distrito}
                    onChange={handleChange}
                />
                <input
                    type="number"
                    name="añosDeMinisterio"
                    placeholder="Años de ministerio"
                    value={formData.añosDeMinisterio}
                    onChange={handleChange}
                />
            </div>
            <button type="submit">Registrarse</button>
        </form>
    );
};

export default Register;
