import React, { useState } from "react";
import "./Login.css";

const Login = () => {
    const [formData, setFormData] = useState({
        correo: "",
        contraseña: "",
    });

    const [message, setMessage] = useState(""); // Mensaje de éxito o error

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3000/api/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData),
            });

            const data = await response.json();
            if (response.ok) {
                setMessage("Inicio de sesión exitoso");
                // Redirigir al dashboard u otra página
                window.location.href = "/dashboard"; // Cambia esta ruta según tu aplicación
            } else {
                setMessage(data.message);
            }
        } catch (error) {
            console.error("Error en el inicio de sesión:", error);
            setMessage("Error al iniciar sesión. Intenta nuevamente.");
        }
    };

    return (
        <div className="login-container">
            <h1 className="login-title">Iniciar Sesión</h1>
            <form className="login-form" onSubmit={handleSubmit}>
                <input
                    type="email"
                    name="correo"
                    placeholder="Correo"
                    value={formData.correo}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="contraseña"
                    placeholder="Contraseña"
                    value={formData.contraseña}
                    onChange={handleChange}
                />
                <button type="submit" className="login-button">Iniciar sesión</button>
            </form>
            <a href="/register" className="login-link">Registrarse</a>
            {message && <p className="login-message">{message}</p>}
        </div>
    );
};

export default Login;
