import React from "react";
import "./MainHeader.css"; // Archivo CSS para estilos
import searchIcon from "../assets/icons/12.svg";

const MainHeader = ({ user }) => {
  return (
    <header className="main-header">
      {/* Mensaje de bienvenida */}
      <div className="welcome-section">
        <h1 className="welcome-title">
          Bienvenido, <span className="user-name">{user.nombre}</span>
        </h1>
        <p className="welcome-subtitle">Herramienta de administración ministerial</p>
      </div>

      {/* Barra de búsqueda */}
      <div className="search-section">
        <input
          type="text"
          placeholder="Buscar líder"
          className="search-input"
        />
        <img src={searchIcon} alt="Search Icon" className="search-icon" />
      </div>

      {/* Información del usuario */}
      <div className="user-info">
        <span className="user-name">{user.nombre}</span>
        <span className="user-role">{user.rol}</span>
        <img
          className="user-avatar"
          src={user.avatar || "https://via.placeholder.com/40"}
          alt="User Avatar"
        />
      </div>
    </header>
  );
};

export default MainHeader;
